import { useContext, useState } from 'react'
import { useCookies } from 'react-cookie'
import { CartQuantityContext, CartContext, MessageContext } from '../../AppContainer'


export default function Item({ product }) {
    const [cookie, setCookie] = useCookies()
    const [cart, setCart] = useContext(CartContext)
    const [message, setMessage] = useContext(MessageContext)
    const [cartQuantity, setCartQuantity] = useContext(CartQuantityContext)
    const [quantity, selectQuantity] = useState(product.cartQuantity)

    function handleChangeItemQuantity(event) {
        switch (event.target.id) {
            case 'subtract':
                fetch('/api/users/removeFromCart', {
                    method: 'PATCH',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ username: cookie.user, cart: [{id: product.state.id, quantity: 1}]})
                }).then(result => result.json())
                .then(cart => {
                    setCartQuantity(cart.totalQuantity)
                    setCart(cart)
                    selectTypeOfPopup('SUCCESS')
                    setMessage('Thêm vào giỏ thành công')
                    showPopup()
                })
                .catch(error => console.log(error))
                break

            case 'plus':
                fetch('/api/users/addToCart', {
                    method: 'PATCH',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ username: cookie.user, cart: [{id: product.state.id, quantity: 1}]})
                }).then(result => result.json())
                .then(cart => {
                    setCartQuantity(cart.totalQuantity)
                    setCart(cart)
                    selectTypeOfPopup('SUCCESS')
                    setMessage('Thêm vào giỏ thành công')
                    showPopup()
                })
                .catch(error => console.log(error))
                break

            case 'removeCartItem':
                fetch('/api/users/addToCart', {
                    method: 'PATCH',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ username: cookie.user, cart: [{id: product.state.id, quantity}]})
                }).then(result => result.json())
                .then(cart => {
                    setCartQuantity(cart.totalQuantity)
                    setCart(cart)
                    selectTypeOfPopup('SUCCESS')
                    setMessage('Thêm vào giỏ thành công')
                    showPopup()
                })
                .catch(error => console.log(error))
                break
        }
    }

    return (
        <div id="cartItem">
            <div id="itemThumnail">
                <img src={ product.thumnail } />
            </div>
            <p style={{ width: '35%'}}>{ product.name }</p>
            <h4 id="unitPrice" >{new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price) }</h4>
            <div id="itemQuantity" style={{ height: '100%', display: 'flex'}}>
                <div id='selectQuantity' style={{ width: '40%', height: '40%', margin: 'auto' }}>
                    <div style={{ fontWeight: 'bold', width: '25%', display: 'flex' }} onClick={() => quantity === 1 ? {} : selectQuantity(quantity - 1) }>
                        <p id='subtract' onClick={ handleChangeItemQuantity }>-</p>
                    </div>
                    <hr style={{ margin: '0.4em 0' }} />
                    <p id='quantity' style={{ fontWeight: 'bold', width: '50%', textAlign: 'center' }}>{quantity}</p>
                    <hr style={{ margin: '0.4em 0' }} />
                    <div style={{ fontWeight: 'bold', width: '25%', display: 'flex' }} onClick={() => selectQuantity(quantity + 1) }>
                        <p id='plus' onClick={ handleChangeItemQuantity } >+</p>
                    </div>
                </div>
                <p id='removeCartItem' onClick={ handleChangeItemQuantity } >Xóa</p>
            </div>
        </div>
    )
}
