import { useCookies } from 'react-cookie'
import './header.css'
import { useEffect, useState } from 'react'

export default function Header() {
    const [cookie, setCookie] = useCookies()
    const [user, setUser] = useState('Đăng nhập')

    useEffect(() => {
        ((typeof cookie.user) !== 'undefined' ? setUser(cookie.user) : setUser('Đăng nhập'))
    }, [cookie.user])

    return (
        <div id="header">
            <div id='logo'>
                <a href='/'><img src='logo.jpg' alt='logo' /></a>
            </div>
            <div id='search'>
                <input type="text" placeholder="Nhập từ khóa để tìm sản phẩm"/>
            </div>
            <div id='userInfo'>
                <div id='cart'>
                    <img src='cart.png' alt='cart' />
                </div>
                <a href='/login' style={{textDecoration: 'underline', color: '#0474e4', margin: 'auto'}}>{ user }</a>
            </div>
        </div>
    )
}
