    useEffect(() => {
        fetch('/api/products/getProductsById', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ productID: item.id })
        })
        .then(res => res.json())
        .then(results => {
        results.map(result => (
            <Item key={ result._id } product={ result }/>
        ))
        })
        .catch((error) => console.log(error))
    }, []) 
