import { useCookies } from 'react-cookie'
import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import './header.css'


export default function Header() {
    const [cookie, setCookie, removeCookie] = useCookies()
    const [user, setUser] = useState('Đăng nhập')
    const navigate = useNavigate()

    useEffect(() => {
        if ((typeof cookie.user) !== 'undefined') {
            setUser((cookie.user).substring(0, 7) + '...')
            document.getElementById('logout').style.display = 'block'
        }
        else {
            setUser('Đăng nhập')
            document.getElementById('logout').style.display = 'none'
        }
    }, [cookie.user])

    function handleLogout() {
        fetch('/api/auth/logout', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
        .then((response) => {
            if (response.status === 200) {
                removeCookie('user')
                navigate('/', { replace: true })
            }
            else {
                console.log('Logout failed!!')
            }
        })
        .catch((err) => {
            console.log(err)
        })
    }

    return (
        <div id="header">
            <div id='logo'>
                <a href='/'><img src='logo.jpg' alt='logo' /></a>
            </div>
            <div id='search'>
                <input type="text" placeholder="Nhập từ khóa để tìm sản phẩm"/>
            </div>
            <div id='userInfo'>
                <div id='cart'>
                    <p id='productQuantityInCart'>0</p>
                    <img src='cart.png' alt='cart' />
                </div>
                <div id='user'>
                    <a href='/login' style={{textDecoration: 'underline', color: '#0474e4', margin: 'auto', whiteSpace: 'nowrap', textAlign: 'center'}}>{ user }</a>
                    <img id='logout' onClick={ handleLogout } src='logout.png' alt='logout' />
                </div>
            </div>
        </div>
    )
}
