import { useLocation, useNavigate } from 'react-router-dom'
import { useCookies } from 'react-cookie'
import { useEffect, useRef, useState } from 'react'
import './cartManage.css'
import Item from './item'

export default function CartManage () {
    const [cookie, setCookie, removeCookie] = useCookies()
    const navigate = useNavigate()
    const products = useLocation().state
    const [address, setAddress] = useState('')
    const [totalPrice, setTotalPrice] = useState(0)
    const price = useRef()

    useEffect(() => {
        products.forEach(item => {
            // setTotalPrice(totalPrice + (item.price * item.cartQuantity))
            price.current = item.price * item.cartQuantity
    })
    }, [])

    // useEffect(() => {
    //     console.log(totalPrice)
    // }, [totalPrice])

    return (
        <div id="cartManage">
            <h2>Các sản phẩm đang chờ trong giỏ hàng</h2>
            <div id='cartItemsContainer'>
                <div id='cartListHeader' >
                    <h3 style={{ width: '50%', textDecoration: 'underline' }}>Sản phẩm</h3>
                    <hr />
                    <h3 style={{ width: '25%', textDecoration: 'underline' }}>Đơn giá</h3>
                    <hr />
                    <h3 style={{ width: '25%', textDecoration: 'underline' }}>Số lượng</h3>
                </div>
                {products.map(product => (
                    <Item key={ product._id } product={ product } />
                ))}
            </div>
            <div id='paymentInfo' style={{ position: 'sticky', bottom: '0px', zIndex: '3', WebkitBackdropFilter: 'blur(5px)', backdropFilter: 'blur(5px)', padding: '1em 0'}}>
                { products.length === 0 ? <h3>Giỏ hàng của bạn đang trống</h3> : <>
                    <div id='addressConfirm'>
                        <h4 style={{ textDecoration: 'underline' }}>Địa chỉ giao hàng:</h4>
                        <p>Địa chỉ của bạn</p>
                    </div>
                    <div id='purchaseContainer'>
                        <h3 style={{ textDecoration: 'underline', marginBottom: '1em' }}>Thành tiền: {new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(price.current) }</h3>
                        <div id='purchase' onClick={ () => navigate('/checkout', {
                            state: products
                        })}>Thanh toán</div>
                    </div>
                </> }
            </div>
        </div>
    )
}
