import { useEffect, useState } from "react"
import { useLoaderData } from "react-router-dom"
import './updateUserInfo.css'

export default function UpdateUserInfo() {
    const provinces = useLoaderData().results
    const [province, setProvince] = useState('')
    const [districts, setDistricts] = useState([])
    const [district, setDistrict] = useState('')
    const [ward, setWard] = useState('')
    const [street, setStreet] = useState('')

    function handleChange(event) {
        switch (event.target.name) {
            case 'province':
                setProvince(event.target.value)
                break
        }
    }

    useEffect(() => {
        if (province !== '') {
            fetch('https://vapi.vnappmob.com/api/province/district/'.concat(province))
            .then(res => res.json())
            .then(results => setDistricts(results.results))
            .catch(err => console.log(err))
        }
    }, [province])

    function saveInfo() {

    }

    return (
        <div id="updateUnserInfo">
            <div id='addressInfo'>
                <h3 style={{ textDecoration: 'underline' }}>Thông tin địa chỉ</h3>
                <div className='inputProductInfo'>
                    <label >Tỉnh / Thành phố:</label>
                    <br />
                    <select name="province" required value={ province } onChange={ handleChange } style={{ padding: '0 0.5em' }}>
                        <option style={{ fontStyle: 'italic' }} >--Chọn tỉnh / thành phố--</option>
                        { provinces.map(province => (
                            <option key={province.province_id} value={province.province_id} >{ (province.province_name).replace(/Tỉnh|Thành phố/g, '') }</option>
                        )) }
                    </select>
                </div>
                <div className='inputProductInfo'>
                    <label >Quận / Huyện / Thị xã:</label>
                    <br />
                    <select name="district" required value={ district } onChange={ handleChange } style={{ padding: '0 0.5em' }}>
                        <option style={{ fontStyle: 'italic' }} >--Chọn quận / huyện / thị xã--</option>
                        {districts.length !== 0 ? districts.map(district => (
                            <option key={district.district_id} >{ (district.district_name).replace(/Huyện|Quận|Thị xã/g, '') }</option>
                        )) : <></> }
                    </select>
                </div>
            </div>
            <div id='optionalInfo'>

            </div>
            <h3 id='submitAddProduct' onClick={ saveInfo }>Lưu thông tin</h3>

        </div>
    )
}
