import { useLocation } from 'react-router-dom'
import './cartManage.css'
import { useEffect, useState } from 'react'
import Item from './item'

export default function CartManage () {
    const [cartItems, setCartItems] = useState([])
    const cart = useLocation().state
    useEffect(() => {
        let idArr = []
        cart.cartItems.forEach(item => {
            idArr.push(item.id)
        })
        fetch('/api/products/getProductsById', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ productID: idArr })
        }).then(res => res.json())
        .then(products => setCartItems(products))
        .catch(error => console.log(error))
    }, [])

    return (
        <div id="cartManage">
            <h2>Các sản phẩm đang chờ trong giỏ hàng</h2>
            <div id='cartItemsContainer'>
                <div id='cartListHeader'>
                    <h3>Sản phẩm</h3>
                    <h3>Đơn giá</h3>
                    <h3>Số lượng</h3>
                </div>
                {cartItems.map((item, index) => (
                    <Item key={ item._id } product={ item } quantity={ cart.cartItems[index].quantity }/>
                ))}
            </div>
        </div>
    )
}
