import { useLocation } from 'react-router-dom'
import './cartManage.css'
import Product from '../product/product'
import { useEffect, useRef } from 'react'
import Item from './item'

export default function CartManage () {
    let idArr = []
    var products = useRef([])

    useLocation().state.cartItems.forEach(item => {
        idArr.push(item.id)
    })
    useEffect(() => {
        fetch('/api/products/getProductsById', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ productID: idArr })
        })
      .then(res => res.json())
      .then(results => products = results)
      .catch((error) => console.log(error))
    }, [])
    return (
        <div id="cartManage">
            <h2>Các sản phẩm đang chờ trong giỏ hàng</h2>
            <div id='cartItemsContainer'>
                {products.map(item => (
                    <p>{ item }</p>
                ))}
            </div>
        </div>
    )
}
