export default function Item({ product, quantity }) {
    return (
        <div id="item">
            <div id="productInfo">
                <div id="itemThumnail">
                    <img src={ product.thumnail } />
                </div>
            </div>
            <h4 id="unitPrice">{new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(product.price) }</h4>
            <h4>{ quantity }</h4>
        </div>
    )
}
