import { useState, createContext, useCallback } from 'react';
import { BrowserRouter, RouterProvider } from 'react-router-dom';
import { CookiesProvider } from 'react-cookie';
import './index.css';
import Footer from './components/footer/footer';
import router from './router/router';
import Banner from './components/banner/banner';
import App from './App';

export const CartQuantityContext = createContext()
export const CartContext = createContext()

export default function AppContainer() {
    const [cartQuantity, setCartQuantity]  = useState(0)
    const [cart, setCart] = useState([])
    const [message, setMessage] = useState("")

    const showPopup = useCallback(() => {
            document.getElementById('warningPopup').style.transform = 'translateY(-10vh)'
            setTimeout(() => {
                document.getElementById('warningPopup').style.transform = 'translateY(-50vh)'
            }, 2500)
        }
    )

  return (
    <CookiesProvider>
      <CartQuantityContext.Provider value={[cartQuantity, setCartQuantity]}>
        <CartContext.Provider value={[cart, setCart]}>
          <Popup message={ message }/>
          <MessageContext.Provider value={ [message, setMessage] }>
            <RouterProvider router={ router } callback={ showPopup } />
          </MessageContext.Provider>
        </CartContext.Provider>
      </CartQuantityContext.Provider>
      <Footer />
    </CookiesProvider>
  );
}
